Component({properties:{name:{type:String,value:"Mr.KLeo"},position:{type:String,value:"前端工程师/Dancer"},description:{type:String,value:"大家好，我是Mr.KLeo，一名正在学习前端的Person。热爱技术，热爱交流，热爱生活。擅长使用Vue、JavaScript、HTML、CSS、Yeo-components开发可复用的视图组件，希望与你共同进步！"},cardPic:{type:String,value:"https://s3.ax1x.com/2021/03/09/63FF1O.jpg"},btnBgColor:{type:String,value:"#7AB2DC"},clickBtnBgColor:{type:String,value:"#5E6675"}},lifetimes:{attached(){this.setData({BgColor:this.data.btnBgColor})}},data:{isAttention:!1},methods:{clickBtn(){let e=this.data.clickBtnBgColor,n=this.data.btnBgColor;this.data.isAttention?(wx.showToast({icon:"none",title:"取消关注"}).then(t=>{this.setData({BgColor:n,isAttention:!1})}),this.triggerEvent("attentevent",{isAttent:!1})):(wx.showToast({icon:"none",title:"感谢关注"}).then(t=>{this.setData({BgColor:e,isAttention:!0})}),this.triggerEvent("attentevent",{isAttent:!0}))}}});